<?php
require_once($CFG->libdir.'/badgeslib.php');
require_once($CFG->dirroot.'/mod/coursebadges/CourseBadges.php');

class CoursesBadgesNotification
{
    private $id;
    private $userid;
    private $coursebadge;
    private $course;
    private $badges;

    public function __construct($id)
    {
        global $USER;

        $this->id = $id;
        $this->userid = $USER->id;
        $this->coursebadge = $this->get_coursebadge_instance();
        $this->course = get_course($this->coursebadge->course);
    }

    public function send_notification()
    {
        global $DB;

        if($this->coursebadge->notification != CourseBadges::VALIDATED_CHOICE_NOTIF
            && $this->coursebadge->notification != CourseBadges::UPDATED_CHOICE_NOTIF) {
            return false;
        }
        if($this->coursebadge->notification == CourseBadges::VALIDATED_CHOICE_NOTIF && $this->has_already_notif_badge()){
            return false;
        }
        $site = get_site();
        $subject = get_string('notification_validated_choice', 'coursebadges', $this->coursebadge->name);
        if ($this->coursebadge->notification == CourseBadges::UPDATED_CHOICE_NOTIF) {
            $subject = get_string('notification_updated_choice', 'coursebadges', $this->coursebadge->name);
        }

        $usersto = CoursesBadgesNotification::get_users_with_role_on_context($this->course->id, 'Formateur');
        $affecteduser = $DB->get_record('user', ['id'=>$this->userid]);

        if (count($this->badges) > 0) {
            foreach($usersto as $userto){
                $notificationmessagetext = $this->notification_make_mail_text($userto, $affecteduser, $this->badges);
                $notificationmessagehtml = $this->notification_make_mail_html($userto, $affecteduser, $this->badges);

                // Send the email now!
                email_to_user($userto, $site->shortname, $subject, $notificationmessagetext, $notificationmessagehtml);
            }
            $this->create_notif_badge();
            return true;
        }

        return false;
    }

    private function get_coursebadge_instance()
    {
        $coursebadge = new CourseBadges($this->id);
        $this->badges = $coursebadge->get_all_selected_badges_instance_by_user();
        return $coursebadge->get_course_badges_instance();
    }

    private function has_already_notif_badge(){
        global $DB;

        $sql = 'SELECT * 
                FROM {coursebadges_notification}
                WHERE coursebadgeid = :coursebadgeid
                AND userid = :userid
                AND type = :type
                LIMIT 1';
        return $DB->get_record_sql($sql,
            ["coursebadgeid" => $this->id, "userid" => $this->userid, "type" => $this->coursebadge->notification]);
    }

    private function create_notif_badge()
    {
        global $DB;
        $new_notif_badge = new stdClass();
        $new_notif_badge->coursebadgeid = $this->id;
        $new_notif_badge->userid = $this->userid;
        $new_notif_badge->badges = CourseBadges::generateListIds($this->badges);
        $new_notif_badge->type = $this->coursebadge->notification;
        $new_notif_badge->sended = 1;
        $new_notif_badge->timemodified = time();

        return $DB->insert_record('coursebadges_notification', $new_notif_badge);
    }

    /**
     * Builds and returns the body of the email notification in plain text.
     *
     * @global object
     * @global object
     * @uses CONTEXT_MODULE
     * @param object $userto
     * @return string The email body in plain text format.
     */
    private function notification_make_mail_text($userto, $affecteduser, $badges)
    {
        $text = "\n---------------------------------------------------------------------\n";

        $a = new stdClass();
        $a->username = $userto->firstname . " " . $userto->lastname;
        $a->affectedusername = $affecteduser->firstname . " " . $affecteduser->lastname;
        $a->course = $this->course->fullname;

        $intro = get_string('email_validated_message_intro', 'coursebadges', $a);
        if ($this->coursebadge->notification == CourseBadges::UPDATED_CHOICE_NOTIF) {
            $intro = get_string('email_updated_message_intro', 'coursebadges', $a);
        }
        $text .= $intro;
        $text .= "\n";
        $text .= "\n---------------------------------------------------------------------\n";

        foreach ($badges as $badge) {
            $url_badge = new moodle_url('badges/overview.php', ['id'=>$badge->id]);
            $text .= '- ' . $badge->name . ' ('.$url_badge->out().')' . "\n" . '';
        }
        $text .= "\n";
        $text .= "\n---------------------------------------------------------------------\n";

        $cm_coursebadges = get_coursemodule_from_instance('coursebadges', $this->coursebadge->id, $this->course->id);
        $url = new moodle_url('mod/coursebadges/view.php', ['id'=>$cm_coursebadges->id]);
        $text_outro = $this->course->fullname . ' ('.$url->out().')';
        $text .= get_string('email_message_outro', 'coursebadges', $text_outro);
        $text .= "\n\n";

        return $text;
    }

    /**
     * Builds and returns the body of the email notification in html format.
     *
     * @global object
     * @param object $userto
     * @return string The email text in HTML format
     */
    private function notification_make_mail_html($userto, $affecteduser, $badges)
    {
        global $CFG;
        $context_course = context_course::instance($this->course->id);
        $html = html_writer::tag('head', '');

        $a = new stdClass();
        $a->username = $userto->firstname . " " . $userto->lastname;
        $a->affectedusername = $affecteduser->firstname . " " . $affecteduser->lastname;
        $a->coursename = $this->course->fullname;

        $intro = get_string('email_validated_message_intro', 'coursebadges', $a);
        if ($this->coursebadge->notification == CourseBadges::UPDATED_CHOICE_NOTIF) {
            $intro = get_string('email_updated_message_intro', 'coursebadges', $a);
        }
        $html .= $intro;

        $html .= html_writer::start_tag('ul', ['class' => 'list-group']);
        foreach ($badges as $badge) {

            $fs = get_file_storage();
            $file = $fs->get_file($context_course->id, 'badges', 'badgeimage', $badge->id, '/', 'f1.png');

            if (!$file) {
                continue; // No such file.
            }
            $localfilepath = $CFG->dataroot.'/filedir/'.substr($file->get_contenthash(),0,2).'/'.substr($file->get_contenthash(),2,2).'/'.$file->get_contenthash();
            $type = pathinfo($localfilepath, PATHINFO_EXTENSION);
            $data = file_get_contents($localfilepath);
            $base64 = 'data:image/' . $type . ';base64,' . base64_encode($data);


            $html .= html_writer::start_tag('li',
                ['style' => 'list-style-type: none;padding-bottom: 10px;',
                    'id' => 'badge_'.$badge->id,
                    'value' => $badge->id]);
            $html .= html_writer::start_div('badge-content',['style'=> 'font-size: 10px;color: #0095db;width: 15%;text-align: center;']);
            $html .= html_writer::img($base64,$badge->name,['style'=> 'width: 100%;']);
            $url = new moodle_url('badges/overview.php', ['id'=>$badge->id]);
            $name = html_writer::span($badge->name,"badge-title");
            $html .= html_writer::link($CFG->wwwroot.'/'.$url->out(false),$name);
            $html .= html_writer::end_div();
            $html .= html_writer::end_tag('li');
        }
        $html .= html_writer::end_tag('ul');

        $cm_coursebadges = get_coursemodule_from_instance('coursebadges', $this->coursebadge->id, $this->course->id);
        $url = new moodle_url('mod/coursebadges/view.php', ['id'=>$cm_coursebadges->id]);

        $a = new stdClass();
        $a->activityname = $this->coursebadge->name;
        $a->activitylink = $CFG->wwwroot.'/'.$url->out();

        $html .= get_string('email_message_outro', 'coursebadges', $a);

        $img_footer = '<div style="margin:auto;display:inline-block;">
            <img src="data:image/png;base64,">
            <img style="margin-left: 10px;" src="data:image/jpeg;base64," >
            </div>';
        $html .= html_writer::tag('footer', $img_footer, ['style' => "text-align:center;"]);

        return $html;
    }

    static function get_users_with_role_on_context($course_id, $rolename){
        global $DB;

        $context = context_course::instance($course_id);
        $role = $DB->get_record('role', array('shortname' => $rolename));

        $sql = "SELECT u.*
                FROM {user} u
                INNER JOIN {role_assignments} ra ON (u.id = ra.userid)
                INNER JOIN {role} r ON (r.id = ra.roleid)
                WHERE ra.contextid = :contextid 
                AND r.name = :rolename";

        return $DB->get_records_sql($sql,["contextid" => $context->id, "rolename" => $role->name]);
    }
}